import{_ as i,c as l,o as e,a}from"./app.ac1e80d5.js";const m=JSON.parse(`{"title":"\u54F2\u5B66","description":"","frontmatter":{},"headers":[{"level":2,"title":"paradigim","slug":"paradigim","link":"#paradigim","children":[{"level":3,"title":"\u4FDD\u4F4F\u672C\u91D1\uFF0C\u4E0D\u8981\u5F52\u96F6","slug":"\u4FDD\u4F4F\u672C\u91D1\uFF0C\u4E0D\u8981\u5F52\u96F6","link":"#\u4FDD\u4F4F\u672C\u91D1\uFF0C\u4E0D\u8981\u5F52\u96F6","children":[]},{"level":3,"title":"\u957F\u671F\u4E3B\u4E49--\u5BF9\u6760\u6746\u8BF4\u4E0D No Margin","slug":"\u957F\u671F\u4E3B\u4E49-\u5BF9\u6760\u6746\u8BF4\u4E0D-no-margin","link":"#\u957F\u671F\u4E3B\u4E49-\u5BF9\u6760\u6746\u8BF4\u4E0D-no-margin","children":[]},{"level":3,"title":"\u4FDD\u6301\u5197\u4F59","slug":"\u4FDD\u6301\u5197\u4F59","link":"#\u4FDD\u6301\u5197\u4F59","children":[]},{"level":3,"title":"\u67D0\u4E9B\u5FC5\u7136\u6027","slug":"\u67D0\u4E9B\u5FC5\u7136\u6027","link":"#\u67D0\u4E9B\u5FC5\u7136\u6027","children":[]},{"level":3,"title":"don't trust, verify.","slug":"don-t-trust-verify","link":"#don-t-trust-verify","children":[]}]},{"level":2,"title":"\u6446\u8131\u522B\u4EBA\u7684\u53D9\u4E8B","slug":"\u6446\u8131\u522B\u4EBA\u7684\u53D9\u4E8B","link":"#\u6446\u8131\u522B\u4EBA\u7684\u53D9\u4E8B","children":[]},{"level":2,"title":"Method","slug":"method","link":"#method","children":[]},{"level":2,"title":"action","slug":"action","link":"#action","children":[]},{"level":2,"title":"avoid ruin","slug":"avoid-ruin","link":"#avoid-ruin","children":[]}],"relativePath":"01_life/index.md"}`),t={name:"01_life/index.md"},o=a('<h1 id="\u54F2\u5B66" tabindex="-1">\u54F2\u5B66 <a class="header-anchor" href="#\u54F2\u5B66" aria-hidden="true">#</a></h1><p><strong>core words:\u8106\u5F31\u4E0E\u53CD\u8106\u5F31\uFF0C trust and verify, \u8BC1\u5B9E\u4E0E\u8BC1\u4F2A\uFF0C \u795E\u79D8\u4E3B\u4E49, \u6295\u8D44</strong></p><h2 id="paradigim" tabindex="-1">paradigim <a class="header-anchor" href="#paradigim" aria-hidden="true">#</a></h2><div class="warning custom-block"><p class="custom-block-title">\u6295\u8D44-\u683C\u96F7\u5384\u59C6</p><p>\u6295\u8D44\u64CD\u4F5C\u662F\u4EE5\u6DF1\u5165\u5206\u6790\u4E3A\u57FA\u7840\uFF0C\u786E\u4FDD\u672C\u91D1\u7684\u5B89\u5168\uFF0C\u5E76\u83B7\u5F97\u9002\u5F53\u7684\u56DE\u62A5--\u683C\u96F7\u5384\u59C6</p><p>An investment operation is one which, upon thorough analysis promises safety of principal and an adequate return. Operations not meeting these requirements are speculative.\u201D</p></div><div class="tip custom-block"><p class="custom-block-title">\u6295\u8D44\u4E0E\u6295\u8D44</p><p>\u6295\u8D44: \u8FFD\u6C42\u4EF7\u503C\u548C\u7ADE\u4E89\u4F18\u52BF\u548C\u53CD\u8106\u5F31, \u5373\u4F7F\u4EF7\u683C\u4E0D\u6DA8\uFF0C\u76C8\u5229\u589E\u957F\uFF0C\u5206\u7EA2\u90FD\u80FDcover.</p><p>\u6295\u8D44\u7684\u6210\u529F\u662F\u53D8\u6210\u4E00\u4E2A\u66F4\u597D\u7684\u4EBA\u7684\u81EA\u7136\u7ED3\u679C</p><p>\u6295\u673A: \u8FFD\u6C42\u4EF7\u683C\u7684\u8FDB\u4E00\u6B65\u9AD8\u4F30.</p></div><div class="warning custom-block"><p class="custom-block-title">don&#39;t trust, veirfy!</p><p><strong>verify/\u8BC1\u5B9E/\u8BC1\u4F2A</strong> Naval: \u6211\u4E0D\u77E5\u9053\u8FD9\u6837\u884C\u4E0D\u884C\uFF0C\u7136\u800C\uFF0C\u6211\u4F1A\u628A\u4E0D\u884C\u7684\u6392\u9664\u6389. I don\u2019t believe I have the ability to say what is going to work. Rather,I try to eliminate what\u2019s not going to work.</p><p>\u3010\u5361\u5C14\u6CE2\u666E\u5C14\uFF1A 1.\u8FD9\u4E2A\u4E16\u754C\uFF0C\u53EA\u6709\u5426\u5B9A\u7684\u7406\u8BBA\u662F\u4E25\u683C\u6210\u7ACB\u7684\u3002 2.\u5F52\u7EB3\u6CD5\u65E2\u4E0D\u80FD\u7ED9\u4EBA\u4EEC\u4EE5\u672A\u6765\u7684\u5FC5\u8981\u6027\u77E5\u8BC6\uFF0C\u4E5F\u4E0D\u80FD\u7ED9\u4EBA\u4EEC\u4EE5\u672A\u6765\u7684\u6216\u7136\u6027\u77E5\u8BC6\u3002\u3011</p><p>\u3010\u5854\u52D2\u5E03\uFF1A 1.\u8FC7\u53BB\u7684\u5386\u53F2\u65E0\u6CD5\u9884\u6D4B\u672A\u6765\u3002\u53EA\u8981\u4E00\u4E2A\u53CD\u4F8B\uFF0C\u4F60\u6DF1\u4FE1\u4E0D\u7591\u7684\u4FE1\u5FF5\u5C31\u4F1A\u5D29\u584C\u3002 2.\u552F\u6709\u88AB\u5426\u5B9A\u7684\u4E8B\u5B9E\u624D\u786E\u5B9A\u65E0\u7591\u3002\u3011</p></div><ul><li>\u884C\u52A8\u6539\u53D8\u601D\u7EF4, \u601D\u7EF4\u6539\u53D8\u884C\u52A8.</li><li>\u957F\u671F\u4E3B\u4E49, \u5236\u9020\u5197\u4F59\u548C\u591A\u6837\u6027(\u53CD\u8106\u5F31)\u3002(\u601D\u7EF4\u6A21\u5F0F\u4E0E\u601D\u8003\u65B9\u5411) \u8001\u5316\uFF1A\u662F\u5197\u4F59\u7684\u6D88\u5931\uFF0C\u65F6\u95F4\u5197\u4F59\uFF0C\u5065\u5EB7\u5197\u4F59(\u5982\u679C\u4F60\u4EC0\u4E48\u90FD\u4E0D\u505A\uFF0C\u4E09\u4E2A\u6708/\u4E09\u5E74\u5BB6\u91CC\u7684\u751F\u6D3B\u4F1A\u5982\u4F55\uFF1F)</li><li>\u968F\u673A\u6027\uFF0C\u5747\u503C\u56DE\u5F52(\u9ED1\u5929\u9E45\uFF0C\u767D\u5929\u9E45\u4E4B\u540E\u7684\u5747\u503C\u56DE\u5F52, \u6301\u4E4B\u4EE5\u6052\u7684\u80FD\u529B-\u5916\u79D1\u533B\u751F\uFF0C\u5F8B\u5E08), \u8FD0\u6C14(\u7535\u5F71\uFF0C\u4E66\u7C4D\uFF0C\u91D1\u878D\u5371\u673A\uFF0C\u75AB\u60C5,\u6218\u4E89).</li><li>\u672A\u77E5\u4E2D\u51B3\u7B56&amp;\u975E\u9884\u6D4B\u6027\u7684\u4E16\u754C\u89C2, \u4F4E\u6210\u672C\u5C1D\u8BD5\u4E0E\u6709\u9650\u8D4C\u6CE8.</li><li>\u4EF7\u503C(\u4E03\u5927\u7ADE\u4E89\u4F18\u52BF\uFF1A \u89C4\u6A21\u6548\u5E94\uFF0C\u7F51\u7EDC\u6548\u5E94\uFF0C\u53CD\u5411\u5B9A\u4F4D\uFF0C\u5207\u6362\u6210\u672C\uFF0C\u54C1\u724C\u6548\u5E94\uFF0C\u8D44\u6E90\u5784\u65AD\uFF0C\u6D41\u7A0B\u7684\u529B\u91CF),\u5B89\u5168\u8FB9\u9645\uFF0C\u9006\u5411\u6295\u8D44, BTC,ETH</li><li>\u8774\u8776\u7684\u7FC5\u8180\uFF0C\u4E8B\u6545\u5E38\u6001\u7406\u8BBA.</li><li>\u4E0D\u8981\u8FC7\u5EA6\u4F18\u5316\u4E0E\u62DF\u5408(\u4EF7\u6295\u4E0A\u52A0\u6760\u6746),\u524A\u8DB3\u9002\u5C65(\u5DF4\u83F2\u7279\uFF0C\u8292\u683C\u5E2E\u4F60\u4F18\u5316\u8FC7\u4E86\u30022022\u5E74\u7684\u4E2D\u6982\u4EE5\u53CA\u8305\u53F0\u5927\u8DCC. FTX\u9668\u843D\u4E2D\u5BF9\u81EA\u5DF1\u7684\u6760\u6746\u7684\u62C5\u5FC3),</li><li>\u907F\u514D\u6B7B\u5730</li><li></li></ul><h3 id="\u4FDD\u4F4F\u672C\u91D1\uFF0C\u4E0D\u8981\u5F52\u96F6" tabindex="-1">\u4FDD\u4F4F\u672C\u91D1\uFF0C\u4E0D\u8981\u5F52\u96F6 <a class="header-anchor" href="#\u4FDD\u4F4F\u672C\u91D1\uFF0C\u4E0D\u8981\u5F52\u96F6" aria-hidden="true">#</a></h3><ul><li>\u4E0D\u8981\u5750\u7262(\u4E0D\u8981\u51BB\u5361)</li><li>\u4E0D\u8981\u7206\u4ED3\u5F52\u96F6(\u4E0D\u8981\u6760\u6746\uFF0C\u5408\u7EA6)</li><li>\u8D70\u8DEF\uFF0C\u5F00\u8F66\u4E0D\u8981\u5206\u5FC3</li><li>\u4E0D\u8981\u71AC\u591C\u731D\u6B7B</li></ul><h3 id="\u957F\u671F\u4E3B\u4E49-\u5BF9\u6760\u6746\u8BF4\u4E0D-no-margin" tabindex="-1">\u957F\u671F\u4E3B\u4E49--\u5BF9\u6760\u6746\u8BF4\u4E0D No Margin <a class="header-anchor" href="#\u957F\u671F\u4E3B\u4E49-\u5BF9\u6760\u6746\u8BF4\u4E0D-no-margin" aria-hidden="true">#</a></h3><ul><li>\u4FDD\u6301\u5197\u4F59\u4E0D\u662F\u4F4E\u6760\u67461.2, 1.5, \u800C\u662F\u914D\u7F6E\u90E8\u5206\u73B0\u91D1(\u56FD\u503A)(\u6765\u81EAwangchuan)</li><li>Never use margin. Always maintain a minimum percentage of allocation in cash or govt bonds. No exceptions.</li><li>\u5F88\u591A\u65F6\u5019\u6839\u672C\u4E0D\u9700\u8981\u548C\u522B\u4EBA\u8F83\u52B2\uFF0C\u5927\u90E8\u5206\u4EBA\uFF0C\u7A0D\u5FAE\u7ED9\u70B9\u65F6\u95F4\uFF0C\u5C31\u4F1A\u8D70\u4E0A\u81EA\u6211\u6BC1\u706D\u7684\u9053\u8DEF</li><li>\u4F60\u6839\u672C\u4E0D\u7528\u6253\u51FA\u7CBE\u5F69\u7684\u597D\u7403\uFF0C\u53EA\u9700\u65F6\u95F4\uFF0C\u522B\u4EBA\u81EA\u7136\u4F1A\u72AF\u9519\u3002(\u53EA\u9700\u907F\u514D\u635F\u5931\u672C\u91D1, \u81EA\u7136\u5C31\u662F\u6536\u76CA\u7684\u5230\u6765\u3002), \u90E8\u5206\u4E1A\u4F59\u9009\u624B\u72AF\u7684\u9519\u8BEF\u53EB\u505A \u201C\u975E\u53D7\u8FEB\u6027\u5931\u8BEF\u201D\uFF0C\u548C\u5BF9\u624B\u6C34\u5E73\u6CA1\u6709\u5173\u7CFB\uFF0C\u5C31\u662F\u81EA\u5DF1\u592A\u70C2\uFF0C\u6CA1\u6709\u4E25\u683C\u8981\u6C42\u81EA\u5DF1\u3002\u4E1A\u4F59\u9009\u624B\u4E4B\u95F4\uFF0C 80%\u7684\u5206\u6570\u662F\u5931\u8BEF\u7ED9\u5BF9\u624B\u9001\u5206\u3002</li></ul><blockquote><p>Simon Ramo wrote &quot;Extraordinary Tennis for the Ordinary Tennis Player&quot; in which he calculated that 81.5% of tennis points are decided by a player making a mistake. To the extent life resembles tennis, avoiding loss on a regular basis may be a key to winning cumulatively.</p></blockquote><h3 id="\u4FDD\u6301\u5197\u4F59" tabindex="-1">\u4FDD\u6301\u5197\u4F59 <a class="header-anchor" href="#\u4FDD\u6301\u5197\u4F59" aria-hidden="true">#</a></h3><p>\u5BF9\u5197\u4F59\u7684\u4E0D\u53EA\u662F\u601D\u60F3\u4E0A\u7684\u7406\u89E3\uFF0C\u884C\u52A8\u4E0A\u7684\u5B9E\u8DF5\u4E5F\u8981\u5982\u6B64</p><ul><li>\u5F00\u8F66\u7684\u65F6\u5019\u4E0D\u8981\u770B\u624B\u673A\uFF0C\u4FDD\u6301\u9A7E\u9A76\u4E0A\u7684\u5197\u4F59.</li><li>\u4E0D\u8981\u7B49\u5230\u6CB9\u7BB1\u5FEB\u8981\u89C1\u5E95\u624D\u53BB\u52A0\u6CB9\uFF0C\u4FDD\u6301\u6CB9\u7BB1\u7684\u5197\u4F59.</li><li>\u51E1\u4E8B\u63D0\u524D\u4E00\u70B9\uFF0C\u4FDD\u6301\u5197\u4F59</li><li>\u601D\u8003\u4E0A\u7684\u591A\u89C6\u89D2: <ul><li>\u5386\u53F2\u89C6\u89D2</li><li>\u5176\u4ED6\u4EBA\u7684\u89C6\u89D2(\u8001\u624B)</li></ul></li><li>\u522B\u4EBAfomo\u7684\u65F6\u5019\uFF0C\u6781\u5EA6\u770B\u597D\u7684\u65F6\u5019\uFF0C\u867D\u7136\u5F88makesense \u81EA\u5DF1\u4E5F\u770B\u597D\uFF0C\u6216\u8005 \u884C\u60C5\u5927\u5E45\u4E0A\u51B2 \u60F3\u52A0\u4ED3\u7684\u65F6\u5019\uFF0C\u4FDD\u6301\u5197\u4F59\u7684\u601D\u60F3\u548C\u5B9E\u8DF5\u60F3\u4E00\u60F3.</li><li>\u770B\u770B\u8FC7\u53BB\u7684\u5468\u7EBF \u6708\u7EBF\uFF0C\u5E95\u90E8\u662F\u5706\u7684\uFF0C\u4F60\u5C31\u80FD\u5728fomo\u4E2D\u6E05\u9192\u4E00\u4E9B\u3002</li><li>\u5065\u5EB7\u7684\u5197\u4F59\uFF0C\u4E0D\u8981\u62BD\u70DF. \u591A\u953B\u70BC.</li></ul><h4 id="\u65E0\u6CD5\u9884\u6D4B\u7684\u73B0\u5B9E\u4E16\u754C" tabindex="-1">\u65E0\u6CD5\u9884\u6D4B\u7684\u73B0\u5B9E\u4E16\u754C <a class="header-anchor" href="#\u65E0\u6CD5\u9884\u6D4B\u7684\u73B0\u5B9E\u4E16\u754C" aria-hidden="true">#</a></h4><ul><li>2020 Zero Covid\u7684\u6301\u7EED\u65F6\u95F4\u53CA\u7ED3\u675F\u65E0\u6CD5\u9884\u6D4B</li><li>FTX\u7684\u5D29\u584C\u65E0\u6CD5\u9884\u6D4B.</li><li>\u901A\u80C0\u7684\u6301\u7EED\u65F6\u95F4\u7B49\u7B49\u3002</li></ul><h3 id="\u67D0\u4E9B\u5FC5\u7136\u6027" tabindex="-1">\u67D0\u4E9B\u5FC5\u7136\u6027 <a class="header-anchor" href="#\u67D0\u4E9B\u5FC5\u7136\u6027" aria-hidden="true">#</a></h3><ul><li>\u5FC5\u7136\u4F1A\u6709\u9ED1\u5929\u9E45.</li><li>\u65B0\u7684\u9886\u57DF\u4E2D\u5FC5\u7136\u7684\u5B58\u5728\u9C81\u83BD\u7684\u6295\u8D44\u8005:\u4F8B\u5982SBF, 3AC,</li><li>\u5FC5\u7136\u4F1A\u6709\u7ECF\u6D4E\u5371\u673A.</li><li>\u6781\u6743\u793E\u4F1A\u7684\u5FC5\u7136\u4F24\u5BB3\u666E\u901A\u4EBA\u5229\u76CA, \u5386\u53F2\u89C6\u89D2: \u53E4\u62C9\u683C\u7FA4\u5C9B\u4EE5\u53CA.</li><li>\u52A8\u5F97\u591A\uFF0C\u53EF\u80FD\u9519\u7684\u591A\uFF0C\u5E26\u6765\u66F4\u591A\u635F\u5931.</li></ul><blockquote><p>\u5173\u952E\u662F\uFF0C\u5BF9\u4E8E\u8868\u8C61\u4E0A\u7684\u53D8\u5316\uFF0C\u505A\u51FA\u7684\u53CD\u5E94\uFF0C\u65E0\u6CD5\u4FDD\u8BC1\u201C\u6C38\u8FDC\u6B63\u786E\u201D\u3002\u957F\u671F\u770B\uFF0C\u4F60\u7684\u9519\u8BEF\u7684\u53CD\u5E94\u5E26\u6765\u7684\u635F\u5931\uFF0C\u4F1A\u62B5\u6D88\u6389\u6B63\u786E\u53CD\u5E94\u7684\u6536\u76CA\uFF0C\u751A\u81F3\u8FD8\u591A\u3002\u800C\u5982\u679C\u957F\u671F\u575A\u6301\u5BF9\u771F\u5B9E\u7684\u5E95\u5C42\u903B\u8F91\u7684\u63A2\u7D22\u548C\u8FFD\u6C42\uFF0C\u5176\u6700\u7EC8\u6536\u76CA\u5219\u4F1A\u78BE\u538B\u8868\u8C61\u4E0A\u7684\u53D8\u5316\u3002</p></blockquote><blockquote><p>\u4EC0\u4E48\u662F\u771F\u6B63\u91CD\u8981\u7684\u5462\uFF1F \u4F60\u6301\u80A1\u4E94\u5E74\u6216\u5341\u5E74\u7684\u4E1A\u7EE9\u8868\u73B0\uFF0C\u6295\u8D44\u7ED3\u675F\u65F6\u7684\u8D22\u5BCC\u4EF7\u503C\u548C\u521D\u671F\u6295\u5165\u8D44\u91D1\u7684\u5BF9\u6BD4\uFF0C\u4EE5\u53CA\u4E0E\u751F\u6D3B\u5F00\u652F\u9700\u8981\u7684\u5BF9\u6BD4\u3002\u8FD9\u624D\u662F\u91CD\u8981\u7684\u3002 \u6709\u4E9B\u4EBA\u8BF4\uFF0C\u53EA\u8981\u80FD\u5145\u5206\u5229\u7528\u76F8\u5BF9\u4EF7\u503C\u3001\u9884\u6D4B\u5E02\u573A\u548C\u5B8F\u89C2\u4E8B\u4EF6\uFF0C\u5E76\u636E\u6B64\u8FDB\u884C\u9891\u7E41\u4EA4\u6613\u3002\u53EA\u8981\u4E0D\u79BB\u573A\uFF0C\u662F\u4E0D\u662F\u4E5F\u7B97\u957F\u671F\u6295\u8D44\uFF0C\u4E5F\u4F1A\u53D6\u5F97\u6210\u529F\u3002 \u6211\u4E0D\u8FD9\u4E48\u8BA4\u4E3A\u3002\u6295\u8D44\u8005\u5E94\u8BE5\u5177\u6709\u5982\u4E0B\u76EE\u6807\uFF1A\u53C2\u4E0E\u5230\u957F\u671F\u7ECF\u6D4E\u6210\u957F\u548C\u4F01\u4E1A\u8D22\u5BCC\u521B\u9020\u4E2D\u6765\uFF0C\u5145\u5206\u6355\u6349\u590D\u5229\u7684\u5947\u8FF9\u3002</p></blockquote><blockquote><p>\u81EA1926\u5E74\u4EE5\u6765\uFF0CS&amp;P500\u6307\u6570\u7684\u5E73\u5747\u5E74\u56DE\u62A5\u662F10.5%\uFF0C\u90A3\u65F6\u5019\u76841\u7F8E\u5143\uFF0C\u73B0\u5728\u662F1.3\u4E07\u7F8E\u5143\uFF01\u5373\u4F7F\u4E2D\u95F4\u7ECF\u5386\u4E8616\u6B21\u8870\u9000\uFF0C1\u6B21\u5927\u8427\u6761\uFF0C\u6570\u6B21\u6218\u4E89\u5305\u62EC\u4E00\u6B21\u4E16\u754C\u5927\u6218\uFF0C\u4E00\u6B21\u5168\u7403\u6027\u75AB\u60C5\u548C\u6570\u4E0D\u6E05\u7684\u5730\u7F18\u653F\u6CBB\u52A8\u8361. \u628A\u53C2\u4E0E\u957F\u671F\u6210\u957F\u7684\u6295\u8D44\u89C6\u4E3A\u4F60\u7684\u4E3B\u4E1A\uFF0C\u800C\u5404\u79CD\u77ED\u671F\u6295\u8D44\u53EA\u662F\u53EF\u6709\u53EF\u65E0\u7684\u88D9\u8FB9\u3002\u4E3A\u63D0\u9AD8\u56DE\u62A5\u7387\u7684\u884C\u4E3A\uFF0C\u5982\u77ED\u671F\u4EA4\u6613\u3001\u6295\u8D44\u6BD4\u91CD\u8C03\u6574\u3001\u9009\u62E9\u5E02\u573A\u8FDB\u51FA\u65F6\u673A\uFF0C\u672C\u8EAB\u975E\u5E38\u56F0\u96BE\u3002\u4F60\u76F8\u4FE1\u81EA\u5DF1\u80FD\u628A\u8FD9\u4E9B\u6D3B\u505A\u597D\uFF0C\u7B49\u4E8E\u4F60\u76F8\u4FE1\u81EA\u5DF1\u5728\u7EDD\u9876\u806A\u660E\u7684\u4EBA\u4E2D\u95F4\u662F\u6700\u51FA\u7C7B\u62D4\u8403\u7684\u3002\u4E09\u601D\u800C\u884C\uFF0C\u77ED\u671F\u6295\u8D44\u6210\u529F\u9700\u8981\u7684\u95E8\u69DB\u975E\u5E38\u9AD8</p></blockquote><h3 id="don-t-trust-verify" tabindex="-1">don&#39;t trust, verify. <a class="header-anchor" href="#don-t-trust-verify" aria-hidden="true">#</a></h3><ul><li>\u795E\u79D8\u4E3B\u4E49\u8BDD\u672F: cz\u6E05\u4ED3\u957F\u8FDC\u6765\u8BF4\u5BF9FTT\u662F\u5DE8\u5927\u7684\u5229\u597D, \u61C2\u7684\u90FD\u61C2. \u5E01\u5B89\u7684\u4EB2\u513F\u5B50. \u968F\u65F6\u8FDB\u51FADC. \u4E2D\u592E\u90E8\u59D4\u6709\u4EBA.</li><li>LUNA, 3AC, STEPN, FTX(GAPP\u5BA1\u8BA1\uFF0C\u51FA\u5165DC, \u54C8\u4F5B\u5929\u624D), \u5927\u91CF\u7684\u673A\u6784\u5BA2\u6237\u5C06\u8D44\u91D1\u653E\u5728FTX\u4E5F\u4E0D\u80FD\u8BC1\u660E\u5176\u5B89\u5168\u3002</li><li>\u6BCF\u4E2A\u4EBA\u4E0D\u540C\u7684\u5206\u6790\u89D2\u5EA6(\u5E7F\u5EA6\u6BD4\u6DF1\u5EA6\u91CD\u8981)</li><li>\u65C1\u6C0F: \u81EA\u5DF1\u521B\u5EFA\u7684\u5E01\uFF0C\u7528\u6765\u505A\u62B5\u62BC(TRX&#39;s USDD, FTX&#39;s FTT),\u4EA7\u751F\u53E6\u5916\u4E00\u79CD.</li><li>\u8BC1\u5B9E: FTX\u662F\u5B89\u5168\u7684\uFF0C\u5927\u91CF\u7684\u80A1\u4E1C\u90FD\u662F\u8033\u719F\u80FD\u8BE6(\u7EA2\u886B\uFF0CTamesk), \u5927\u91CF\u7684\u4E13\u4E1A\u673A\u6784\u8D44\u91D1\u6258\u7BA1\u5728FTX(\u4E8B\u540E\u62A5\u9053Multicoin,\u53CA\u5176\u4ED6\u5BF9\u51B2\u57FA\u91D1), US GAPP\u5BA1\u8BA1, \u63D0\u5E01\u65E0\u4E0A\u9650\uFF0C\u6D41\u52A8\u6027\u5145\u8DB3\uFF0C \u5408\u89C4,SBF\u968F\u65F6\u8FDB\u51FADC. SBF\u88AB\u9876\u7EA7\u5A92\u4F53\u63CF\u8FF0\u4E3A\u4E00\u4E2A\u5229\u4ED6\u4E3B\u4E49\u8005\uFF0C\u8D5A\u94B1\u662F\u4E3A\u4E86\u6350\u7ED9\u4ED6\u4EBA\uFF0C\u7D20\u98DF\u4E3B\u4E49\u8005\uFF0C\u7A7F\u7740\u5E73\u5E38\u7684\u8863\u670D\uFF0C\u5F00\u7740\u4E8C\u624B\u672C\u7530\u3002</li><li>\u8BC1\u4F2A: CZ\u6E05\u7A7AFTT(\u907F\u514D\u53E6\u4E00\u4E2ALuna.)\uFF0C \u603B\u6709\u4EBA\u80FD\u6709\u4F60\u4E0D\u540C\u7684\u89C6\u89D2.</li><li>verify/Trust: <ul><li>sign</li><li>Merckle Tree proof of reserve.</li></ul></li></ul><h2 id="\u6446\u8131\u522B\u4EBA\u7684\u53D9\u4E8B" tabindex="-1">\u6446\u8131\u522B\u4EBA\u7684\u53D9\u4E8B <a class="header-anchor" href="#\u6446\u8131\u522B\u4EBA\u7684\u53D9\u4E8B" aria-hidden="true">#</a></h2><blockquote><p>The interpretation matters very often a lot more as we\u2019ll see than the information that goes in \u89E3\u8BFB\u6BD4\u4FE1\u606F\u672C\u8EAB\u66F4\u91CD\u8981</p></blockquote><ul><li>\u53D9\u4E8B\u81EA\u7531,\u5EFA\u7ACB\u81EA\u5DF1\u7684paradigim <ul><li>\u7EB5\u5411\u7684\u770B: \u53E4\u4EBA\u7684\u89C6\u89D2\uFF0C\u767E\u5E74\u524D\u7684\u751F\u5B58\u73AF\u5883,\u5386\u53F2\u4E0A\u53D1\u751F\u7684\u4E8B\u60C5?</li><li>\u6A2A\u5411\u7684\u770B\uFF1A \u4F60\u7684\u611F\u89C9VS\u5176\u4ED6\u4EBA\u7684\u611F\u89C9, \u662F\u5426\u8FC7\u4E8E\u81EA\u6211\u4E2D\u5FC3\u4E3B\u4E49\uFF1F</li></ul></li><li>Will Durant \u66FE\u8BF4\u8FC7\uFF1A\u201C\u5386\u53F2\u592A\u4E30\u5BCC\u4E86\uFF0C\u4F60\u8DB3\u4EE5\u4ECE\u5386\u53F2\u4E2D\u5F97\u5230\u4EFB\u4F55\u7ED3\u8BBA\u3002\u201D(\u4E2D\u56FD\u53E4\u8BDD\u6700\u4E3A\u5BB3\u4EBA.)</li><li>\u8FD9\u4E2A\u76D8\u5B50\u80FD\u66B4\u5BCC VS \u8FD9\u4E2A\u4E8B\u7269\u7EC8\u5C06\u6539\u53D8\u4E16\u754C\u300D\uFF0C\u80FD\u8BA9\u53C2\u4E0E\u5176\u4E2D\u7684\u4EBA\u83B7\u5F97\u6536\u76CA\uFF0C\u662F\u7B2C\u4E00\u4FE1\u4EF0\u7684\u4F34\u968F\u526F\u4EA7\u54C1\u3002\u4F46\u300C\u8FD9\u4E2A\u4E8B\u7269\u7EC8\u5C06\u6539\u53D8\u4E16\u754C\u300D\uFF0C\u662F\u9700\u8981\u4E00\u6574\u5957\u77E5\u8BC6\u4F53\u7CFB\u53BB\u786E\u8BA4\u4E86\u4E00\u53E5\u5F52\u7EB3\u3002Code is Law \u5728\u5F88\u7F13\u6162\u7684\u53D1\u5C55\u7740</li><li>\u52E4\u52B3\uFF0C\u5949\u732E\uFF0C\u820D\u8EAB\uFF0C\u4F1F\u5927, \u4EBA\u6C11 \u4EBA\u6027\u53EF\u4EE5\u6539\u9020 VS \u4EBA\u6027(\u4E0D\u5B8C\u7F8E)\u63A5\u53D7\u4EBA\u6027</li><li>\u82F1\u96C4 VS \u6CA1\u6709\u82F1\u96C4</li><li>\u4E00\u5207\u90FD\u662F\u4E3A\u4F60\u597D <ul><li>\u4E00\u542C\u201C\u90FD\u662F\u4E3A\u4F60\u597D\u201D\u8FD9\u53E5\u8BDD\u5C31\u72AFPTSD\uFF0C\u5C24\u5176\u5F53\u7740\u9762\u8BF4\uFF0C\u6211\u8111\u5B50\u91CC\u552F\u4E00\u7684\u5FF5\u5934\u53EA\u6709\uFF1A\u8FD9\u5B59\u5B50\u6253\u7B97\u5751\u6211\uFF0C\u4F2A\u5584\u5305\u88F9\u7740\u5A01\u80C1\u548C\u6B3A\u9A97\uFF0C\u4E3A\u8FBE\u76EE\u7684\u505A\u4EBA\u505A\u4E8B\u6CA1\u4EC0\u4E48\u5E95\u7EBF\uFF0C\u865A\u4F2A\u53C8\u4E0B\u8D31.\u4E3A\u4EC0\u4E48\u4E3A\u6211\u597D\u6211\u81EA\u5DF1\u5374\u611F\u53D7\u4E0D\u5230\uFF0C\u540E\u6765\u60F3\u660E\u767D\u4E86\uFF0C\u8FD9\u53E5\u8BDD\u539F\u672C\u5E94\u8BE5\u662F\u201C\u4F60\u662F\u50BB\u903C\uFF0C\u5DF2\u7ECF\u50BB\u903C\u5230\u4E0D\u77E5\u9053\u4EC0\u4E48\u597D\u4EC0\u4E48\u4E0D\u597D\u4E86\uFF0C\u6240\u4EE5\u6211\u624D\u51FA\u4E8E\u5584\u5FC3\u66FF\u4F60\u505A\u4E2A\u51B3\u5B9A(\u53EA\u505A\u51B3\u5B9A\u4E0D\u5B9E\u9645\u5E2E\u5FD9)\uFF0C\u90FD\u662F\u4E3A\u4F60\u597D\u554A\u201D\uFF0C\u4ED6\u4EEC\u53EA\u8BF4\u4E86\u6700\u540E\u7684\u7ED3\u8BBA\uFF0C\u6CA1\u8BF4\u524D\u9762\u7684\u60F3\u6CD5\u3002</li></ul></li></ul><h2 id="method" tabindex="-1">Method <a class="header-anchor" href="#method" aria-hidden="true">#</a></h2><p><strong>1. \u8D22\u5BCC(\u8D44\u4EA7)</strong></p><p>\u8D44\u4EA7\u662F\u80FD\u4EA7\u751F\u7761\u540E\u6536\u5165\u7684\u90E8\u5206.(ETH-USDC LP, BTC-ETH LP\u7684\u624B\u7EED\u8D39\u6536\u5165),\u5FC5\u987B\u8981\u62E5\u6709\u8D44\u4EA7.</p><p><strong>2. \u65B9\u5F0F</strong></p><ul><li>\u5B66\u4E60Selling and Building.\u80FD\u89C4\u6A21\u5316\u7684\u521B\u5EFA\u793E\u4F1A/\u793E\u533A\u9700\u8981\u7684\u4E1C\u897F.</li><li>\u590D\u5229\u6548\u5E94:\u77E5\u8BC6\uFF0C\u8D22\u5BCC\uFF0C\u8FD0\u52A8\u90FD\u662F\u80FD\u591F\u590D\u5229\u7684.\u548C\u957F\u671F\u7684\u4EBA\u4E00\u8D77\u73A9\u957F\u671F\u7684\u884C\u4E1A.</li><li>\u6B66\u88C5\u81EA\u5DF1\uFF1A\u7279\u5B9A\u7684\u77E5\u8BC6\uFF0C\u8D23\u4EFB\u5FC3\uFF0C\u6760\u6746. <ul><li>coding(\u6760\u6746\uFF0C\u80FD\u81EA\u52A8\u5316), \u8F93\u51FA\u5FAE\u535A\uFF0C\u7F51\u7AD9, podcast</li><li>\u5B66\u4E60\u5FAE\u89C2\u7ECF\u6D4E\u5B66\uFF0C\u535A\u5F08\u8BBA\uFF0C\u5FC3\u7406\u5B66\uFF0C\u8BF4\u670D\u529B\uFF0C\u4F26\u7406\u5B66\uFF0C\u6570\u5B66\uFF0C\u548C\u8BA1\u7B97\u673A\u3002</li><li>\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u578B(\u4F8B\u5982\u62A4\u57CE\u6CB3\uFF0C\u7F51\u7EDC\u6548\u5E94\uFF0C\u5F00\u653E\u793E\u4F1A)</li></ul></li></ul><h2 id="action" tabindex="-1">action <a class="header-anchor" href="#action" aria-hidden="true">#</a></h2><ul><li>\u8DD1\u6B65\uFF0C\u8DF3\u7EF3\uFF0C\u5F15\u4F53\u5411\u4E0A\uFF0C\u6BCF\u5929\u3002\u575A\u6301\u7684\u65F6\u95F4</li><li>BTC, ETH, AAVE.</li><li>\u9605\u8BFB\u4E0E\u5386\u53F2, \u53D9\u4E8B\u81EA\u7531.</li><li>coding\u5B66\u4E60\u4E0E\u6559\u80B2. js,ts, github, golang, blockchain.</li><li>\u82F1\u8BED, youtube\u8282\u76EE.</li><li>\u6BCF\u5468\u6E05\u6D17\u53A8\u623F</li><li>\u5C11\u62BD\u70DF\uFF0C\u6212\u70DF</li><li>\u5199\u4F5C/coding. \u5C06\u81EA\u5DF1\u7684\u4E00\u90E8\u5206\u8F6C\u5316\u6210 \u7845\u57FA\u751F\u547D.</li></ul><h2 id="avoid-ruin" tabindex="-1">avoid ruin <a class="header-anchor" href="#avoid-ruin" aria-hidden="true">#</a></h2><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>\u5982\u679C\u6211\u77E5\u9053\u6211\u4F1A\u6B7B\u5728\u54EA\u513F\uFF0C\u6211\u5C31\u4E0D\u4F1A\u53BB\u90A3\u513F\u3002</p></div><p>The one thing you have to avoid is the risk of ruin.</p><blockquote><ul><li>stay out of jail</li><li>stay out of total catastrophic loss</li><li>stay out of things that could be physically dangerous or hurt your body</li><li>stay out of things that could cause to lose all of your capital, all of your savings.</li><li>Don\u2019t gamble everything on one go.</li></ul></blockquote><p>I don\u2019t believe I have the ability to say what is going to work. Rather,I try to eliminate what\u2019s not going to work. I think being successful is just about not making mistakes. It\u2019s not about having correct judgement. It\u2019s about avoiding incorrect judgements.</p>',39),n=[o];function r(s,d,u,h,c,p){return e(),l("div",null,n)}const v=i(t,[["render",r]]);export{m as __pageData,v as default};
