import{_ as i,c as l,o as a,a as t}from"./app.b37d644a.js";const m=JSON.parse('{"title":"\u54F2\u5B66","description":"","frontmatter":{},"headers":[{"level":2,"title":"paradigim\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u5F0F","slug":"paradigim\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u5F0F","link":"#paradigim\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u5F0F","children":[]},{"level":2,"title":"Method","slug":"method","link":"#method","children":[]},{"level":2,"title":"action","slug":"action","link":"#action","children":[]},{"level":2,"title":"avoid ruin","slug":"avoid-ruin","link":"#avoid-ruin","children":[]}],"relativePath":"life/index.md"}'),e={name:"life/index.md"},o=t('<h1 id="\u54F2\u5B66" tabindex="-1">\u54F2\u5B66 <a class="header-anchor" href="#\u54F2\u5B66" aria-hidden="true">#</a></h1><h2 id="paradigim\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u5F0F" tabindex="-1">paradigim\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u5F0F <a class="header-anchor" href="#paradigim\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u5F0F" aria-hidden="true">#</a></h2><ul><li>\u884C\u52A8\u6539\u53D8\u601D\u7EF4, \u601D\u7EF4\u6539\u53D8\u884C\u52A8.</li><li>\u957F\u671F\u4E3B\u4E49, \u5236\u9020\u5197\u4F59\u548C\u591A\u6837\u6027(\u53CD\u8106\u5F31)\u3002(\u601D\u7EF4\u6A21\u5F0F\u4E0E\u601D\u8003\u65B9\u5411) \u8001\u5316\uFF1A\u662F\u5197\u4F59\u7684\u6D88\u5931\uFF0C\u65F6\u95F4\u5197\u4F59\uFF0C\u5065\u5EB7\u5197\u4F59(\u5982\u679C\u4F60\u4EC0\u4E48\u90FD\u4E0D\u505A\uFF0C\u4E09\u4E2A\u6708/\u4E09\u5E74\u5BB6\u91CC\u7684\u751F\u6D3B\u4F1A\u5982\u4F55\uFF1F)</li><li>\u968F\u673A\u6027\uFF0C\u5747\u503C\u56DE\u5F52(\u9ED1\u5929\u9E45\uFF0C\u767D\u5929\u9E45\u4E4B\u540E\u7684\u5747\u503C\u56DE\u5F52, \u6301\u4E4B\u4EE5\u6052\u7684\u80FD\u529B-\u5916\u79D1\u533B\u751F\uFF0C\u5F8B\u5E08), \u8FD0\u6C14(\u7535\u5F71\uFF0C\u4E66\u7C4D\uFF0C\u91D1\u878D\u5371\u673A\uFF0C\u75AB\u60C5,\u6218\u4E89).</li><li>\u672A\u77E5\u4E2D\u51B3\u7B56&amp;\u975E\u9884\u6D4B\u6027\u7684\u4E16\u754C\u89C2, \u4F4E\u6210\u672C\u5C1D\u8BD5\u4E0E\u6709\u9650\u8D4C\u6CE8.</li><li>\u4EF7\u503C(\u4E03\u5927\u7ADE\u4E89\u4F18\u52BF\uFF1A \u89C4\u6A21\u6548\u5E94\uFF0C\u7F51\u7EDC\u6548\u5E94\uFF0C\u53CD\u5411\u5B9A\u4F4D\uFF0C\u5207\u6362\u6210\u672C\uFF0C\u54C1\u724C\u6548\u5E94\uFF0C\u8D44\u6E90\u5784\u65AD\uFF0C\u6D41\u7A0B\u7684\u529B\u91CF),\u5B89\u5168\u8FB9\u9645\uFF0C\u9006\u5411\u6295\u8D44, BTC,ETH</li><li>\u8774\u8776\u7684\u7FC5\u8180\uFF0C\u4E8B\u6545\u5E38\u6001\u7406\u8BBA.</li><li>\u4E0D\u8981\u8FC7\u5EA6\u4F18\u5316\u4E0E\u62DF\u5408(\u4EF7\u6295\u4E0A\u52A0\u6760\u6746, \u8D44\u672C\u6548\u7387),\u524A\u8DB3\u9002\u5C65(\u5DF4\u83F2\u7279\uFF0C\u8292\u683C\u5E2E\u4F60\u4F18\u5316\u8FC7\u4E86\u30022022\u5E74\u7684\u4E2D\u6982\u4EE5\u53CA\u8305\u53F0\u5927\u8DCC)</li><li>\u907F\u514D\u6B7B\u5730</li></ul><h2 id="method" tabindex="-1">Method <a class="header-anchor" href="#method" aria-hidden="true">#</a></h2><p><strong>1. \u8D22\u5BCC(\u8D44\u4EA7)</strong></p><p>\u8D44\u4EA7\u662F\u80FD\u4EA7\u751F\u7761\u540E\u6536\u5165\u7684\u90E8\u5206.(ETH-USDC LP, BTC-ETH LP\u7684\u624B\u7EED\u8D39\u6536\u5165),\u5FC5\u987B\u8981\u62E5\u6709\u8D44\u4EA7.</p><p><strong>2. \u65B9\u5F0F</strong></p><ul><li>\u5B66\u4E60Selling and Building.\u80FD\u89C4\u6A21\u5316\u7684\u521B\u5EFA\u793E\u4F1A/\u793E\u533A\u9700\u8981\u7684\u4E1C\u897F.</li><li>\u590D\u5229\u6548\u5E94:\u77E5\u8BC6\uFF0C\u8D22\u5BCC\uFF0C\u8FD0\u52A8\u90FD\u662F\u80FD\u591F\u590D\u5229\u7684.\u548C\u957F\u671F\u7684\u4EBA\u4E00\u8D77\u73A9\u957F\u671F\u7684\u884C\u4E1A.</li><li>\u6B66\u88C5\u81EA\u5DF1\uFF1A\u7279\u5B9A\u7684\u77E5\u8BC6\uFF0C\u8D23\u4EFB\u5FC3\uFF0C\u6760\u6746. <ul><li>coding(\u6760\u6746\uFF0C\u80FD\u81EA\u52A8\u5316), \u8F93\u51FA\u5FAE\u535A\uFF0C\u7F51\u7AD9, podcast</li><li>\u5B66\u4E60\u5FAE\u89C2\u7ECF\u6D4E\u5B66\uFF0C\u535A\u5F08\u8BBA\uFF0C\u5FC3\u7406\u5B66\uFF0C\u8BF4\u670D\u529B\uFF0C\u4F26\u7406\u5B66\uFF0C\u6570\u5B66\uFF0C\u548C\u8BA1\u7B97\u673A\u3002</li><li>\u8303\u5F0F\u53CA\u601D\u7EF4\u6A21\u578B(\u4F8B\u5982\u62A4\u57CE\u6CB3\uFF0C\u7F51\u7EDC\u6548\u5E94\uFF0C\u5F00\u653E\u793E\u4F1A)</li></ul></li></ul><h2 id="action" tabindex="-1">action <a class="header-anchor" href="#action" aria-hidden="true">#</a></h2><ul><li>\u8DD1\u6B65\uFF0C\u8DF3\u7EF3\uFF0C\u5F15\u4F53\u5411\u4E0A\uFF0C\u6BCF\u5929\u3002\u575A\u6301\u7684\u65F6\u95F4</li><li>BTC, ETH, AAVE.</li><li>\u9605\u8BFB\u4E0E\u5386\u53F2, \u53D9\u4E8B\u81EA\u7531.</li><li>coding\u5B66\u4E60\u4E0E\u6559\u80B2. js,ts, github, golang, blockchain.</li><li>\u82F1\u8BED, youtube\u8282\u76EE.</li><li>\u6BCF\u5468\u6E05\u6D17\u53A8\u623F</li><li>\u5C11\u62BD\u70DF\uFF0C\u6212\u70DF</li></ul><h2 id="avoid-ruin" tabindex="-1">avoid ruin <a class="header-anchor" href="#avoid-ruin" aria-hidden="true">#</a></h2><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>\u5982\u679C\u6211\u77E5\u9053\u6211\u4F1A\u6B7B\u5728\u54EA\u513F\uFF0C\u6211\u5C31\u4E0D\u4F1A\u53BB\u90A3\u513F\u3002</p></div><p>The one thing you have to avoid is the risk of ruin.</p><blockquote><ul><li>stay out of jail</li><li>stay out of total catastrophic loss</li><li>stay out of things that could be physically dangerous or hurt your body</li><li>stay out of things that could cause to lose all of your capital, all of your savings.</li><li>Don\u2019t gamble everything on one go.</li></ul></blockquote><p>I don\u2019t believe I have the ability to say what is going to work. Rather,I try to eliminate what\u2019s not going to work. I think being successful is just about not making mistakes. It\u2019s not about having correct judgement. It\u2019s about avoiding incorrect judgements.</p>',15),n=[o];function r(d,s,h,c,u,g){return a(),l("div",null,n)}const _=i(e,[["render",r]]);export{m as __pageData,_ as default};
